using System;
using Gtk;

module Main
{
	public Main() : void
	{
		Application.Init();
		_ = MyWindow();
		Application.Run();
	}
}

using System;
using Gtk;

public class MyWindow : Window
{
	public this() 
	{
		base("MyWindow");
		SetDefaultSize(400, 300);
		this.DeleteEvent += (fun (_) { Application.Quit ();} );
		ShowAll();
	}
}

